mapper.select:
SELECT 

FROM ats_section a

WHERE a.id = #{id}

SELECT 

FROM ats_section a

where a.is_del = 0

		AND	a.status = #{status}


		AND	a.pid = #{pid}


		AND a.editor = #{editor}



		ORDER BY ${page.orderBy}




order by a.parse_order

SELECT 

FROM ats_section a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE ATS_SECTION SET 

		eff = #{eff}


		exp = #{exp}


		link = #{link}

WHERE PID = #{pid}
